default:
function sumEvenNumbers(arr) {
  return arr.filter(x => x % 2 = 0).reduce((a,b) => a + b);
}

solution:
function sumEvenNumbers(arr) {
  return arr.filter(x => x % 2 === 0).reduce((a,b) => a + b, 0);
}

default:
function firstAUpper(arr) {
  for (let str in arr) {
    if (str[0] = 'a') {
      return str.toUpperCase;
    }
  }
  return null;
}

solution:
function firstAUpper(arr) {
  for (let str of arr) {
    if (str[0] === 'a') {
      return str.toUpperCase();
    }
  }
  return null;
}

default:
function evenOrOdd(n) {
  if (n % 2 = 0) return 'even',
  else return 'odd';
}

solution:
function evenOrOdd(n) {
  if (n % 2 === 0) return 'even';
  else return 'odd';
}

default:
function countLong(arr) {
  let count = 0;
  for (let str of arr) {
    if str.length > 3 count =+ 1;
  }
  return count;
}

solution:
function countLong(arr) {
  let count = 0;
  for (let str of arr) {
    if (str.length > 3) count += 1;
  }
  return count;
}

default:
function checkNumber(n) {
  if n > 0 return 'yes';
  else if n < 0 return 'no';
  else return 'zero';
}

solution:
function checkNumber(n) {
  if (n > 0) return 'yes';
  else if (n < 0) return 'no';
  else return 'zero';
}

default:
function totalAge(people) {
  let sum = 0;
  for (let person in people) {
    sum += people.person.age;
  }
  return sum;
}

solution:
function totalAge(people) {
  let sum = 0;
  for (let person in people) {
    sum += people[person].age;
  }
  return sum;
}

default:
async function fetchData(url) {
  let res = fetch(url, { method: 'get' });
  return res.json;
}

solution:
async function fetchData(url) {
  let res = await fetch(url, { method: 'GET' });
  return res.json();
}
